import React, { useState, useEffect } from 'react'
import "./TaskManager.css"
import axios from 'axios';

function TaskManager (){
    const [tasks,setTasks] = useState([]);
    const [editingTask, setEditingTask] = useState({
      date:'',
      text:''
    });
    const [newTask, setNewTask] = useState({
      date:'',
      text:'',
      completed: false,
      editing: false,
      isToday: true
    });
    const [isAdding, setIsAdding] = useState(false);
    const [isEditing, setIsEditing] = useState(false);

    useEffect(() => {
      fetchTasks();
    }, [tasks]);

    const handleAddTask = async () => {
        try {
          await axios.post('http://localhost:5000/tasks', newTask);
          console.log(newTask);
          setNewTask({
            date:'',
            text:'',
            completed: false,
            editing: false,
            isToday: true
          });
          fetchTasks();
        } catch (error) {
          console.error('Error adding task:', error);
        }
      };


      const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewTask({
          ...newTask,
          [name]: value
        });
      };

      const fetchTasks = async () => {
        try {
          const response = await axios.get('http://localhost:5000/tasks');
          setTasks(response.data.data);
          console.log(response.data.data); 
        } catch (error) {
          console.error('Error fetching products:', error);
        }
      };
    
      const handleDeleteTask = async (id) => {
        if (window.confirm(`Biztosan törölni szeretnéd?`)) {
          await axios.post('http://localhost:5000/delete', {taskid: id});   
        }  
          fetchTasks();
      };

      const handleEditTaskChange = async (id) => {
        setIsAdding(false)
        const response = await axios.get('http://localhost:5000/edittaskchange', {params:{taskid: id}});
        const thisTaskEditing = response.data.data
        
        setNewTask({
          date:response.data.date,
          text:response.data.text,
          completed: false,
          editing: false,
          isToday: true
        });

        if(isEditing && !thisTaskEditing){
            alert("Fejezd be a jelenlegi módosításaidat!")
        }
        else{
          await axios.post('http://localhost:5000/edittaskchange', {taskid: id})
        setIsEditing(!isEditing)
        if(!isEditing){
          setNewTask({
            date:'',
            text:'',
            completed: false,
            editing: false,
            isToday: true
          });
        }
        }
        
        fetchTasks();
      };

      const handleEditTask = async (id) => {
        
          await axios.post('http://localhost:5000/edittask', {taskid: id, text:newTask.text, date:newTask.date});
          setIsEditing(false);
          handleEditTaskChange(id);
          fetchTasks();
          
          setNewTask({
            date:'',
            text:'',
            completed: false,
            editing: false,
            isToday: true
          });
      };
    
      const handleToggleComplete = async (id) => {
        try {
          await axios.post('http://localhost:5000/togglecomplete', { taskid: id });
          fetchTasks(); 
        } catch (error) {
          console.error('Hiba történt a feladat állapotának módosításakor:', error);
        }
      };

      const handleCheckboxChange = async (event) => {
        try {
          
          if (event.target.checked) {
            await Promise.all(
              tasks.map((task) =>
                isDateToday(task.date)
                  ? Promise.resolve() 
                  : axios.post('http://localhost:5000/istoday', { taskid: task._id, today: false })
              )
            );
          } else {
            
            await Promise.all(
              tasks.map((task) =>
                axios.post('http://localhost:5000/istoday', { taskid: task._id, today: true })
              )
            );
          } 
        } catch (error) {
          console.error('Hiba történt a checkbox állapot frissítésekor:', error);
        }
      };
      

      function isDateToday(inputDate) {
       
        const [inputYear, inputMonth, inputDay] = inputDate.split('-').map(Number);
        const inputDateObj = new Date(inputYear, inputMonth - 1, inputDay);
        const today = new Date();
        const todayDateObj = new Date(today.getFullYear(), today.getMonth(), today.getDate());
      
        
        return inputDateObj.getTime() === todayDateObj.getTime();
      }

      

    return(
        <>
        <div className='taskManager'>
            <div className='inputs'>
            {!isAdding && (
                <button id='addButton' onClick={() => setIsAdding(true)}>Új feladat hozzáadása</button>
            )}

            {isAdding && (
                    <div className='inputdata'>
                    <input
                        type="date"
                        name="date"
                        value={newTask.date}
                        onChange={handleInputChange}
                    />
                    <textarea
                        value={newTask.text}
                        name="text"
                        onChange={handleInputChange}
                        placeholder="Adj hozzá egy feladatot"
                    />
                    <button onClick={handleAddTask}>Hozzáadás</button>
                    <button onClick={() => setIsAdding(false)}>Mégse</button>
                    </div>
                )}
            </div>
            <p><input
                type="checkbox"
                onChange={handleCheckboxChange}
                />
                Mai határidő
                </p>
            <ul>
        {Array.isArray(tasks) && tasks.map((task) => (
            task.isToday && (
          <li key={task.id} style={{ backgroundColor: task.completed ? 'green' : 'rgba(0,0,0,0)' }}>
            {!task.editing && (<div>
            {task.text } | Határidő: {task.date}
            <div className='taskButtons'>
            <button onClick={() => handleToggleComplete(task._id)}>
              {task.completed ? 'Visszaállítás' : 'Kész'}
            </button>
            <button onClick={() => handleDeleteTask(task._id)}>Törlés</button>
            <button onClick={() => handleEditTaskChange(task._id)}>Módosítás</button></div>
            </div>)}
            
            {task.editing && (<div>
                <input
                        type="date"
                        name="date"
                        value={newTask.date}
                        onChange={handleInputChange}
                    />
                    <textarea
                        name="text"
                        value={newTask.text}
                        onChange={handleInputChange}
                        rows="1" 
                        cols="30" 
                    />
                    <div className='taskButtons'>
                    <button onClick={() =>handleEditTask(task._id)}>Hozzáadás</button>
                    <button onClick={() => handleEditTaskChange(task._id)}>Mégse</button></div>
                </div>)} 
          </li>
          )
        ))}
      </ul>
        </div>
        </>
    )

}

export default TaskManager
