import React, { useState } from 'react'
import "./TaskManager.css"


function TaskManager (){
    const [tasks, setTasks] = useState([]);
    const [task, setTask] = useState('');
    const [taskText, setTaskText] = useState('');
    const [deadline,setDeadline] = useState('');
    const [isAdding, setIsAdding] = useState(false);
    const [isEditing, setIsEditing] = useState(false);

    const handleAddTask = () => {
        if (taskText.trim()) {
          setTasks([...tasks, { id: tasks.length + 1,date: deadline, text: taskText, completed: false, editing: false, isToday:true }]);
          setTask('');
        }
      };
    
      const handleDeleteTask = (id) => {
        setTasks(tasks.filter((task) => task.id !== id));
      };

      const handleEditTaskChange = (id) => {
        const thisTask = tasks.find((task) => task.id === id);
        setIsAdding(false)
        if(isEditing && !thisTask.editing){
            alert("Fejezd be a jelenlegi módosításaidat!")
        }
        else{
        setTasks(
            tasks.map((task) =>
              task.id === id ? { ...task, editing: !task.editing } : task
            )
          );
        setIsEditing(!isEditing)
        }
      };

      const handleEditTask = (id) => {
        setTasks(
            tasks.map((task) =>
              task.id === id ? { ...task, date: deadline, text: taskText, editing:false} : task
            )
          );
          setIsEditing(false)
      };
    
      const handleToggleComplete = (id) => {
        setTasks(
          tasks.map((task) =>
            task.id === id ? { ...task, completed: !task.completed } : task
          )
        );
      };

      const handleCheckboxChange = (event) => {
        if(event.target.checked){
            setTasks(
                tasks.map((task) => 
                  isDateToday(task.date) 
                    ? task
                    : { ...task, isToday: false }
                )
              );
        }else{
            setTasks(
                tasks.map((task) => 
                    ({
                        ...task,
                        isToday: true
                      })
                )
              );
            
        }

      };

      function isDateToday(inputDate) {
       
        const [inputYear, inputMonth, inputDay] = inputDate.split('-').map(Number);
        const inputDateObj = new Date(inputYear, inputMonth - 1, inputDay);
      
        
        const today = new Date();
        const todayDateObj = new Date(today.getFullYear(), today.getMonth(), today.getDate());
      
        
        return inputDateObj.getTime() === todayDateObj.getTime();
      }

    return(
        <>
        <div className='taskManager'>
            <div className='inputs'>
            {!isAdding && (
                <button onClick={() => setIsAdding(true)}>Új feladat hozzáadása</button>
            )}

            {isAdding && (
                    <div>
                    <input
                        type="date"
                        value={deadline}
                        onChange={(e) => setDeadline(e.target.value)}
                    />
                    <input
                        type="text"
                        value={taskText}
                        onChange={(e) => setTaskText(e.target.value)}
                        placeholder="Adj hozzá egy feladatot"
                    />
                    <button onClick={handleAddTask}>Hozzáadás</button>
                    <button onClick={() => setIsAdding(false)}>Mégse</button>
                    </div>
                )}
            </div>
            <p><input
                type="checkbox"
                onChange={handleCheckboxChange}
                />
                Mai határidő
                </p>
            <ul>
        {tasks.map((task) => (
            task.isToday && (
          <li key={task.id} style={{ textDecoration: task.completed ? 'line-through' : 'none' }}>
            {!task.editing && (<div>
            {task.text } | Határidő: {task.date}
            <button onClick={() => handleToggleComplete(task.id)}>
              {task.completed ? 'Visszaállítás' : 'Kész'}
            </button>
            <button onClick={() => handleDeleteTask(task.id)}>Törlés</button>
            
            <button onClick={() => handleEditTaskChange(task.id)}>Módosítás</button>
            </div>)}
            
            {task.editing && (<div>
                <input
                        type="date"
                        value={deadline}
                        onChange={(e) => setDeadline(e.target.value)}
                    />
                    <input
                        type="text"
                        value={taskText}
                        onChange={(e) => setTaskText(e.target.value)}
                        placeholder={task.text}
                    />
                    <button onClick={() =>handleEditTask(task.id)}>Hozzáadás</button>
                    <button onClick={() => handleEditTaskChange(task.id)}>Mégse</button>
                </div>)} 
          </li>
          )
        ))}
      </ul>
        </div>
        </>
    )

}

export default TaskManager
