import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./TaskManager.css";

function TaskManager() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({
    date: '',
    text: '',
    completed: false,
    editing: false,
    isToday: true
  });
  const [isAdding, setIsAdding] = useState(false);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetchTasks();
  }, []); // Only fetch tasks on component mount

  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/tasks');
      setTasks(response.data.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleAddTask = async () => {
    try {
      await axios.post('http://localhost:5000/tasks', newTask);
      setNewTask({
        date: '',
        text: '',
        completed: false,
        editing: false,
        isToday: true
      });
      setIsAdding(false); // Hide the add task form
      await fetchTasks(); // Refresh tasks after adding
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTask({
      ...newTask,
      [name]: value
    });
  };

  const handleDeleteTask = async (id) => {
    if (window.confirm('Biztosan törölni szeretnéd?')) {
      try {
        await axios.post('http://localhost:5000/delete', { taskid: id });
        await fetchTasks(); // Refresh tasks after deleting
      } catch (error) {
        console.error('Error deleting task:', error);
      }
    }
  };

  const handleEditTaskChange = async (id) => {
    if (isEditing) {
      alert("Fejezd be a jelenlegi módosításaidat!");
      return;
    }
    try {
      const response = await axios.get('http://localhost:5000/edittaskchange', { params: { taskid: id } });
      setNewTask({
        date: response.data.date,
        text: response.data.text,
        completed: false,
        editing: true,
        isToday: true
      });
      setIsEditing(true);
    } catch (error) {
      console.error('Error fetching task for editing:', error);
    }
  };

  const handleEditTask = async (id) => {
    try {
      await axios.post('http://localhost:5000/edittask', { taskid: id, text: newTask.text, date: newTask.date });
      setIsEditing(false);
      setNewTask({
        date: '',
        text: '',
        completed: false,
        editing: false,
        isToday: true
      });
      await fetchTasks(); // Refresh tasks after editing
    } catch (error) {
      console.error('Error editing task:', error);
    }
  };

  const handleToggleComplete = async (id) => {
    try {
      await axios.post('http://localhost:5000/togglecomplete', { taskid: id });
      setTasks(prevTasks =>
        prevTasks.map(task =>
          task._id === id ? { ...task, completed: !task.completed } : task
        )
      );
    } catch (error) {
      console.error('Error toggling task completion:', error);
    }
  };

  const handleCheckboxChange = async (event) => {
    try {
      await Promise.all(
        tasks.map(task =>
          axios.post('http://localhost:5000/istoday', { taskid: task._id, today: event.target.checked })
        )
      );
      await fetchTasks(); // Refresh tasks after updating 'isToday' status
    } catch (error) {
      console.error('Error updating today status:', error);
    }
  };

  return (
    <div className='taskManager'>
      <div className='inputs'>
        {!isAdding && (
          <button id='addButton' onClick={() => setIsAdding(true)}>Új feladat hozzáadása</button>
        )}
        {isAdding && (
          <div className='inputdata'>
            <input
              type="date"
              name="date"
              value={newTask.date}
              onChange={handleInputChange}
            />
            <textarea
              value={newTask.text}
              name="text"
              onChange={handleInputChange}
              placeholder="Adj hozzá egy feladatot"
            />
            <button onClick={handleAddTask}>Hozzáadás</button>
            <button onClick={() => setIsAdding(false)}>Mégse</button>
          </div>
        )}
      </div>
      <p>
        <input
          type="checkbox"
          onChange={handleCheckboxChange}
        />
        Mai határidő
      </p>
      <ul>
        {tasks.map((task) => (
          task.isToday && (
            <li key={task._id} style={{ backgroundColor: task.completed ? 'green' : 'rgba(0,0,0,0)' }}>
              {!task.editing && (
                <div>
                  {task.text} | Határidő: {task.date}
                  <div className='taskButtons'>
                    <button onClick={() => handleToggleComplete(task._id)}>
                      {task.completed ? 'Visszaállítás' : 'Kész'}
                    </button>
                    <button onClick={() => handleDeleteTask(task._id)}>Törlés</button>
                    <button onClick={() => handleEditTaskChange(task._id)}>Módosítás</button>
                  </div>
                </div>
              )}
              {task.editing && (
                <div>
                  <input
                    type="date"
                    name="date"
                    value={newTask.date}
                    onChange={handleInputChange}
                  />
                  <textarea
                    name="text"
                    value={newTask.text}
                    onChange={handleInputChange}
                    rows="1"
                    cols="30"
                  />
                  <div className='taskButtons'>
                    <button onClick={() => handleEditTask(task._id)}>Módosítás</button>
                    <button onClick={() => handleEditTaskChange(task._id)}>Mégse</button>
                  </div>
                </div>
              )}
            </li>
          )
        ))}
      </ul>
    </div>
  );
}

export default TaskManager;
